def ROUGE(docs):
    
    _TOP_NUM = 100
    _REF_TOTAL_GRAMS = 0
        
    (counts, docs_bigrams) = PMI(docs)
    
    for doc_bigrams in docs_bigrams:
        _REF_TOTAL_GRAMS += len(doc_bigrams)
    
    i = 0
    hits = 0
    for word, value in sorted(counts.items(), key=lambda x: x[1], reverse=True):
        if i > _TOP_NUM:
            break 
        #print(word, value)
        """   reference match """
#         for doc in docs:
#             words = preprocessing(doc)
#             bi_words = list(nltk.bigrams(words))
#             _REF_TOTAL_GRAMS += len(bi_words)
#             for (x,y) in bi_words:
#                 if word is x+' '+y:
#                     hits +=1
        for doc_bigrams in docs_bigrams:
            for bigram in doc_bigrams:
                if word is bigram:
                    hits+=1
        i+=1
    
    #return hits
    return hits/_REF_TOTAL_GRAMS


